$(function () {

    $(".J_tabLeft").on("click", a);
    $(".J_tabRight").on("click", b);
    $(".J_menuItem").on("click", c);
    $(".J_menuTabs").on("dblclick", ".J_menuTab", d);
    $(".J_menuTabs").on("click", ".J_menuTab", e);
    $(".J_menuTabs").on("click", ".J_menuTab i", h);
    $(".J_tabCloseOther").on("click", i);
    $(".J_tabShowActive").on("click", j);

    function f(l) {
        var k = 0;
        $(l).each(function () {
            k += $(this).outerWidth(true)
        });
        return k
    }

    function g(n) {
        var o = f($(n).prevAll()),
            q = f($(n).nextAll());
        var l = f($(".content-tabs").children().not(".J_menuTabs")) + 40;
        var k = $(".content-tabs").outerWidth() - l;
        var p = 0;
        if ($(".page-tabs-content").outerWidth() < k) {
            p = 0
        } else {
            if (q <= (k - $(n).outerWidth(true) - $(n).next().outerWidth(true))) {
                if ((k - $(n).next().outerWidth(true)) > q) {
                    p = o;
                    var m = n;
                    while ((p - $(m).outerWidth()) > ($(".page-tabs-content").outerWidth() - k)) {
                        p -= $(m).prev().outerWidth();
                        m = $(m).prev()
                    }
                }
            } else {
                if (o > (k - $(n).outerWidth(true) - $(n).prev().outerWidth(true))) {
                    p = o - $(n).prev().outerWidth(true)
                }
            }
        }
        $(".page-tabs-content").animate({
                marginLeft: 0 - p + "px"
            },
            "fast")
    }

    function a() {
        var o = Math.abs(parseInt($(".page-tabs-content").css("margin-left")));
        var l = f($(".content-tabs").children().not(".J_menuTabs")) + 40;
        var k = $(".content-tabs").outerWidth() - l;
        var p = 0;
        if ($(".page-tabs-content").width() < k) {
            return false
        } else {
            var m = $(".J_menuTab:first");
            var n = 0;
            while ((n + $(m).outerWidth(true)) <= o) {
                n += $(m).outerWidth(true);
                m = $(m).next()
            }
            n = 0;
            if (f($(m).prevAll()) > k) {
                while ((n + $(m).outerWidth(true)) < (k) && m.length > 0) {
                    n += $(m).outerWidth(true);
                    m = $(m).prev()
                }
                p = f($(m).prevAll())
            }
        }
        $(".page-tabs-content").animate({
                marginLeft: 0 - p + "px"
            },
            "fast")
    }

    function b() {
        var o = Math.abs(parseInt($(".page-tabs-content").css("margin-left")));
        var l = f($(".content-tabs").children().not(".J_menuTabs")) + 40;
        var k = $(".content-tabs").outerWidth() - l;
        var p = 0;
        if ($(".page-tabs-content").width() < k) {
            return false
        } else {
            var m = $(".J_menuTab:first");
            var n = 0;
            while ((n + $(m).outerWidth(true)) <= o) {
                n += $(m).outerWidth(true);
                m = $(m).next()
            }
            n = 0;
            while ((n + $(m).outerWidth(true)) < (k) && m.length > 0) {
                n += $(m).outerWidth(true);
                m = $(m).next()
            }
            p = f($(m).prevAll());
            if (p > 0) {
                $(".page-tabs-content").animate({
                        marginLeft: 0 - p + "px"
                    },
                    "fast")
            }
        }
    }

    $(".J_menuItem").each(function (k) {
        if (!$(this).attr("data-index")) {
            $(this).attr("data-index", k)
        }
    });

    function c() {
        var o = $(this).attr("href");
        var m = $(this).data("index");
        var l = $.trim($(this).text());
        var k = true;
        if (o == undefined || $.trim(o).length == 0) {
            return false
        }
        $(".J_menuTab").each(function () {
            if ($(this).data("id") == o) {
                if (!$(this).hasClass("active")) {
                    $(this).addClass("active").siblings(".J_menuTab").removeClass("active");
                    g(this);
                    $(".J_mainContent .J_iframe").each(function () {
                        if ($(this).data("id") == o) {
                            $(this).show().siblings(".J_iframe").hide();
                            return false
                        }
                    })
                }
                k = false;
                return false
            }
        });
        if (k) {
            var p = '<a href="javascript:;" class="active J_menuTab" data-id="' + o + '">' + l + ' <i class="fa fa-times-circle"></i></a>';
            $(".J_menuTab").removeClass("active");
            var n = '<iframe class="embed-responsive-item J_iframe" name="iframe' + m + '" src="' + o + '" frameborder="0" data-id="' + o + '" seamless></iframe>';
            $(".embed-responsive-16by9").find("iframe").hide().parents(".embed-responsive-16by9").append(n);
            $(".J_menuTabs .page-tabs-content").append(p);
            g($(".J_menuTab.active"))
        }
        return false
    }

    function h() {
        var m = $(this).parents(".J_menuTab").data("id");
        var l = $(this).parents(".J_menuTab").width();
        if ($(this).parents(".J_menuTab").hasClass("active")) {
            if ($(this).parents(".J_menuTab").next(".J_menuTab").size()) {
                var k = $(this).parents(".J_menuTab").next(".J_menuTab:eq(0)").data("id");
                $(this).parents(".J_menuTab").next(".J_menuTab:eq(0)").addClass("active");
                $(".J_mainContent .J_iframe").each(function () {
                    if ($(this).data("id") == k) {
                        $(this).show().siblings(".J_iframe").hide();
                        return false
                    }
                });
                var n = parseInt($(".page-tabs-content").css("margin-left"));
                if (n < 0) {
                    $(".page-tabs-content").animate({
                            marginLeft: (n + l) + "px"
                        },
                        "fast")
                }
                $(this).parents(".J_menuTab").remove();
                $(".J_mainContent .J_iframe").each(function () {
                    if ($(this).data("id") == m) {
                        $(this).remove();
                        return false
                    }
                })
            }
            if ($(this).parents(".J_menuTab").prev(".J_menuTab").size()) {
                var k = $(this).parents(".J_menuTab").prev(".J_menuTab:last").data("id");
                $(this).parents(".J_menuTab").prev(".J_menuTab:last").addClass("active");
                $(".J_mainContent .J_iframe").each(function () {
                    if ($(this).data("id") == k) {
                        $(this).show().siblings(".J_iframe").hide();
                        return false
                    }
                });
                $(this).parents(".J_menuTab").remove();
                $(".J_mainContent .J_iframe").each(function () {
                    if ($(this).data("id") == m) {
                        $(this).remove();
                        return false
                    }
                })
            }
        } else {
            $(this).parents(".J_menuTab").remove();
            $(".J_mainContent .J_iframe").each(function () {
                if ($(this).data("id") == m) {
                    $(this).remove();
                    return false
                }
            });
            g($(".J_menuTab.active"))
        }
        return false
    }

    function i() {
        $(".page-tabs-content").children("[data-id]").not(":first").not(".active").each(function () {
            $('.J_iframe[data-id="' + $(this).data("id") + '"]').remove();
            $(this).remove()
        });
        $(".page-tabs-content").css("margin-left", "0")
        $("ul[role='menu']").hide()
    }

    function j() {
        g($(".J_menuTab.active"))
        $("ul[role='menu']").hide()
    }

    function e() {
        if (!$(this).hasClass("active")) {
            var k = $(this).data("id");
            $(".J_mainContent .J_iframe").each(function () {
                if ($(this).data("id") == k) {
                    $(this).show().siblings(".J_iframe").hide();
                    return false
                }
            });
            $(this).addClass("active").siblings(".J_menuTab").removeClass("active");
            g(this)
        }
    }

    function d() {
        var l = $('.J_iframe[data-id="' + $(this).data("id") + '"]');
        var k = l.attr("src")
    }

    $(".J_tabCloseAll").on("click",
        function () {
            $(".page-tabs-content").children("[data-id]").not(":first").each(function () {
                $('.J_iframe[data-id="' + $(this).data("id") + '"]').remove();
                $(this).remove()
            });
            $(".page-tabs-content").children("[data-id]:first").each(function () {
                $('.J_iframe[data-id="' + $(this).data("id") + '"]').show();
                $(this).addClass("active")
            });
            $(".page-tabs-content").css("margin-left", "0")
            $("ul[role='menu']").hide()
        })

    $(".J_tabClose").on("click",
        function () {
            if ($("ul[role='menu']").is(":hidden")) {
                $("ul[role='menu']").show()
            } else {
                $("ul[role='menu']").hide()
            }

        })

    $(window).resize(function () {
        /*if(document.body.clientHeight<700){
            window.resizeTo(document.body.clientHeight,300);
        }*/
        if (document.body.clientWidth < 500) {
            $("li.dropdown").hide();
            $(".mega-dropdown").hide();
            /*window.resizeTo(document.body.clientWidth,300);*/
        } else {
            $("li.dropdown").show();
            $(".mega-dropdown").show();
        }
        if (document.body.clientWidth < 400) {
            $(".dropdown.J_tabClose").hide();
            $(".J_tabRight").css("right", "40px");
            $(".btn-group.roll-nav.roll-right").css("right", "0px");
        } else {
            $(".dropdown.J_tabClose").show();
            $(".J_tabRight").css("right", "120px");
            $(".btn-group.roll-nav.roll-right").css("right", "40px");
        }
        //iframe 页面高度调整 cmchen 2018.06.20 start
        var height = document.body.offsetHeight;
        $("#iframe").css("padding-bottom",height-75);
        //iframe 页面高度调整 cmchen 2018.06.20 end
    });
});